mapper.update:
update
	   mail_tag
   set 
   	   tag_name = #{tagName}
     , tag_color = #{tagColor}
 where tag_no = #{tagNo}

mapper.insert:
insert
  into mail
     (
       mail_no
     , mail_title
     , mail_content
     , sender
     , receiver
     , mail_ref
     )
values
	 (
	   seq_mail.nextval
	 , #{mailTitle}
	 , #{mailContent}
	 , #{sender}
	 , #{receiver}
	 , #{mailRef}
	 )

insert
  into mail_status
     (
       mail_no
     , user_mail
     , user_name
     , mail_type
     )
values
	 (
	   seq_mail.currval
	 , #{userMail}
	 , #{userName}
	 , #{mailType}
	 )

insert
  into mail
     (
       mail_no
     , mail_title
     , mail_content
     , sender
     , receiver
     , mail_ref
     , send_status
     )
values
	 (
	   seq_mail.nextval
	 , #{mailTitle}
	 , #{mailContent}
	 , #{sender}
	 , #{receiver}
	 , #{mailRef}
	 , 'N'
	 )

mapper.update:
update
	   mail
   set 
       mail_title = #{mailTitle}
     , mail_content = #{mailContent}
     , receiver = #{receiver}
     , mail_ref = #{mailRef}
 where mail_no = #{mailNo}
   and send_status = 'N'

update
	   mail
   set 
       mail_title = #{mailTitle}
     , mail_content = #{mailContent}
     , receiver = #{receiver}
     , mail_ref = #{mailRef}
     , send_status = 'Y'
 where mail_no = #{mailNo}
   and send_status = 'N'

mapper.select:
select mail_no , mail_title , mail_content , sender , m.user_name as "sender_name" , receiver , mail_ref , to_char(send_date, 'yyyy-mm-dd day hh24:mi:ss') as "send_date" , mail_important , tag_no , tag_name , tag_color from mail join mail_status s using (mail_no) left join member m on (sender = email) left join mail_tag using (tag_no) where mail_no = #{mailNo} and s.user_mail = #{email}
and mail_type in (1, 3)
and mail_type = 2

mapper.update:
update
       mail_status
   set mail_important = #{type}
 where mail_no = #{mailNo}
   and user_mail = #{email}

update
       mail_status
   set mail_read = #{type}
 where mail_no = #{mailNo}
   and user_mail = #{email}

		update
		       mail_status
		   set ${type} = 'Y'
		 where mail_no in

           #{no}

		   and user_mail = #{email}

mapper.select:
select sender
  from (
            select sender
                 , (
                     select count(*)
                      from mail_spam
                     where user_mail = #{email}
                       and spam_address = sender
                   ) count
              from mail
             where mail_no in

	           #{no}

       )
 where count = 0

mapper.insert:
insert
  into mail_spam
     (
       spam_no
     , user_mail
     , spam_address
     )
values
     (
       seq_spam.nextval
     , #{email}
     , #{sender}
     )

